import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import { getAllBusiness } from "../../../api/businesses";
import { Table, notification, Breadcrumb, Tag, Image } from "antd";
import ComMenuButonTable from "../../../Components/ComMenuButonTable/ComMenuButonTable";

const formatDate = (dateString) => {
  if (!dateString) return "N/A";
  const date = new Date(dateString);
  return date.toLocaleString("vi-VN", {
    day: "2-digit",
    month: "2-digit",
    year: "numeric",
    hour: "2-digit",
    minute: "2-digit",
  });
};

const getStatusColor = (status) => {
  switch (status) {
    case "ACTIVE":
      return "success";
    case "PENDING":
      return "warning";
    case "INACTIVE":
      return "error";
    default:
      return "default";
  }
};

const TableBusiness = () => {
  const navigate = useNavigate();
  const [businesses, setBusinesses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [pagination, setPagination] = useState({
    current: 1,
    pageSize: 10,
    total: 0,
  });

  const columns = [
    {
      title: "Tên doanh nghiệp",
      dataIndex: "name",
      key: "name",
      width: 400,
      render: (text, record) => (
        <div className="flex items-center space-x-3">
          <div className="w-16 h-16 flex-shrink-0">
            {" "}
            {/* Tăng kích thước từ w-10 h-10 lên w-16 h-16 */}
            <Image
              src={record.imageUrl}
              alt={text}
              className="rounded-full object-cover w-full h-full"
              preview={false}
              fallback="data:image/png;base64,"
            />
          </div>
          <span className="ml-2">{text}</span>{" "}
          {/* Thêm margin-left để tạo khoảng cách với text */}
        </div>
      ),
    },
    {
      title: "Số điện thoại",
      dataIndex: "phone",
      key: "phone",
    },
    {
      title: "Tổng dịch vụ",
      dataIndex: "toTalServiceNum",
      key: "toTalServiceNum",
      width: 120,
    },
    {
      title: "Ngày đăng ký gói",
      dataIndex: "registeredTime",
      key: "registeredTime",
      render: (date) => formatDate(date),
    },
    {
      title: "Ngày hết hạn gói",
      dataIndex: "expiredTime",
      key: "expiredTime",
      render: (date) => formatDate(date),
    },
    {
      title: "Trạng thái",
      dataIndex: "status",
      key: "status",
      render: (status) => (
        <Tag color={getStatusColor(status)}>
          {status === "ACTIVE"
            ? "Hoạt động"
            : status === "PENDING"
            ? "Chờ duyệt"
            : "Ngừng hoạt động"}
        </Tag>
      ),
    },
    {
        title: "",
        key: "operation",
        fixed: "right",
        width: 80,
        render: (_, record) => (
          <ComMenuButonTable
            record={record}
            showModalEdit={() => {
              const userId = record.id;
              navigate(`/admin/store/update/${userId}`);
            }}
            excludeDefaultItems={["delete", "details"]}
          />
        ),
      },
  ];

  const fetchBusinesses = async (page = 1, pageSize = 10) => {
    try {
      setLoading(true);
      const response = await getAllBusiness(false, pageSize, page);
      setBusinesses(response);
      setPagination({
        ...pagination,
        total: response.length, // Cập nhật tổng số items nếu API trả về
        current: page,
        pageSize: pageSize,
      });
    } catch (error) {
      notification.error({
        message: "Lỗi",
        description: "Không thể tải danh sách doanh nghiệp",
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBusinesses();
  }, []);

  const handleTableChange = (pagination) => {
    fetchBusinesses(pagination.current, pagination.pageSize);
  };

  return (
    <div className="bg-white p-4 rounded-lg shadow-sm">
      <Table
        scroll={{ y: "calc(100vh - 250px)" }}
        columns={columns}
        dataSource={businesses}
        loading={loading}
        pagination={pagination}
        onChange={handleTableChange}
        rowKey="id"
        bordered
      />
    </div>
  );
};

export default TableBusiness;
